$(document).ready(function(){document.getElementById("return-to-top").style.display="none",$(window).scroll(function(){100<=$(this).scrollTop()?document.getElementById("return-to-top").style.display="":document.getElementById("return-to-top").style.display="none"}),$("#return-to-top").click(function(){$("body,html").animate({scrollTop:0},700)})});